import React from 'react';
import { BUILDS } from '../constants.tsx';
import type { Workflow } from '../types';
import { FracturedLoopLogo, ArrowUturnLeftIcon, CheckCircleIcon } from './IconComponents';
import { dispatchOpenKnowledgeEvent } from '../services/uiEvents';

interface SidebarProps {
  selectedBuild: string;
  onSelectBuild: (build: string) => void;
  onGoBackToLanding: () => void;
  workflow: Workflow | null;
  completedBuilds: Set<string>;
}

const Sidebar: React.FC<SidebarProps> = ({ selectedBuild, onSelectBuild, onGoBackToLanding, workflow, completedBuilds }) => {
  const handleOpenKnowledge = () => {
    dispatchOpenKnowledgeEvent();
  };

  return (
    <aside className="bg-gray-800/50 w-64 p-4 flex flex-col fixed inset-y-0 left-0">
      <div className="flex items-center gap-2 px-2 mb-4">
        <FracturedLoopLogo className="w-8 h-8" title="Fractured Loop" />
        <h1 className="text-xl font-bold text-gray-100">Fractured Loop</h1>
      </div>

      <button 
        onClick={onGoBackToLanding}
        className="flex items-center gap-3 w-full px-3 py-2 rounded-lg text-left transition-colors duration-200 text-gray-300 hover:bg-gray-700 hover:text-white"
        aria-label="Back to Home"
      >
        <ArrowUturnLeftIcon className="w-5 h-5" />
        <span className="font-medium">Back to Home</span>
      </button>

      <div className="border-t border-gray-700 my-4"></div>

      {workflow && (
        <div className="px-3 mb-3">
          <p className="text-xs uppercase text-gray-500 font-semibold">Workflow</p>
          <h2 className="text-lg font-bold text-indigo-300">{workflow.name}</h2>
        </div>
      )}

      <nav className="flex flex-col gap-2">
        {BUILDS.map((build) => {
          const isSelected = selectedBuild === build.id;
          const isCompleted = completedBuilds.has(build.id);
          const isInWorkflow = workflow ? workflow.builds.includes(build.id) : true;

          if (!isInWorkflow) {
            return null;
          }

          return (
            <button
              key={build.id}
              onClick={() => onSelectBuild(build.id)}
              className={`flex items-center gap-3 px-3 py-2 rounded-lg text-left transition-colors duration-200 ${
                isSelected
                  ? 'bg-indigo-600 text-white shadow-lg'
                  : 'text-gray-300 hover:bg-gray-700 hover:text-white'
              }`}
            >
              {isCompleted ? <CheckCircleIcon className="w-6 h-6" title="Build completed" /> : build.icon}
              <span className={`font-medium ${isCompleted ? 'line-through text-gray-400' : ''}`}>{build.name}</span>
            </button>
          )
        })}
      </nav>
      <button
        onClick={handleOpenKnowledge}
        className="mt-6 px-3 py-2 rounded-lg text-sm font-medium border border-indigo-500/60 text-indigo-200 hover:bg-indigo-600/20 transition-colors"
        type="button"
      >
        Open Knowledge Base
      </button>
      <div className="mt-auto text-center text-xs text-gray-500">
        <p>Powered by Gemini</p>
      </div>
    </aside>
  );
};

export default Sidebar;

loop/components/Timeline.tsx.content:
import React, { useState, useRef, useCallback, useEffect } from 'react';
import type { Project, Track, Layer, TimelineItem } from '../types';

interface TimelineProps {
  project: Project;
  setProject: React.Dispatch<React.SetStateAction<Project>>;
  selectedNodeId: string | null;
  setSelectedNodeId: React.Dispatch<React.SetStateAction<string | null>>;
  selectedConnectionId: string | null;
  setSelectedConnectionId: React.Dispatch<React.SetStateAction<string | null>>;
  onAssetDrop: (assetId: string, trackId: string, startTime: number, layerId?: string) => void;
}

const Timeline: React.FC<TimelineProps> = ({
  project,
  setProject,
  selectedNodeId,
  setSelectedNodeId,
  selectedConnectionId,
  setSelectedConnectionId,
  onAssetDrop
}) => {
  const [selectedItemId, setSelectedItemId] = useState<string | null>(null);
  const [isDragging, setIsDragging] = useState(false);
  const [isResizing, setIsResizing] = useState(false);
  const dragRef = useRef<{ itemId: string; startX: number; startTime: number; startDuration: number; trackId: string; layerId?: string } | null>(null);

  // Extract data from project
  const tracks = project.tracks || [];
  const timelineItems = project.timelineItems || [];
  const assets = project.assets;

  const handleItemMove = (itemId: string, newStartTime: number, newTrackId: string, newLayerId?: string) => {
    setProject(prev => ({
      ...prev,
      timelineItems: prev.timelineItems ? prev.timelineItems.map((item: TimelineItem) =>
        item.id === itemId
          ? { ...item, startTime: newStartTime, trackId: newTrackId, layerId: newLayerId }
          : item
      ) : [],
      updatedAt: new Date()
    }));
  };

  const handleItemResize = (itemId: string, newDuration: number) => {
    setProject(prev => ({
      ...prev,
      timelineItems: prev.timelineItems ? prev.timelineItems.map((item: TimelineItem) =>
        item.id === itemId ? { ...item, duration: newDuration } : item
      ) : [],
      updatedAt: new Date()
    }));
  };

  const handleItemSelect = (itemId: string) => {
    setSelectedItemId(itemId);
    setSelectedNodeId(null);
    setSelectedConnectionId(null);
  };

  // Handle drop on timeline layer
  const handleDrop = useCallback((e: React.DragEvent<HTMLDivElement>, trackId: string, layerId?: string) => {
    e.preventDefault();
    const assetId = e.dataTransfer.getData('text/plain');
    if (!assetId) return;

    // Calculate start time based on drop position
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const startTime = Math.max(0, x / 10); // 10px per second

    onAssetDrop(assetId, trackId, startTime, layerId);
  }, [onAssetDrop]);

  const handleDragOver = useCallback((e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault(); // Allow drop
  }, []);

  // Helper to get asset by id
  const getAssetById = (id: string) => assets.find(a => a.id === id);

  // Global mouse event handlers for drag and resize
  useEffect(() => {
    const handleGlobalMouseMove = (e: MouseEvent) => {
      if (isDragging && dragRef.current) {
        e.preventDefault();
        const deltaX = e.clientX - dragRef.current.startX;
        const deltaSeconds = deltaX / 10; // 10px per second
        const newStartTime = Math.max(0, dragRef.current.startTime + deltaSeconds);
        handleItemMove(dragRef.current.itemId, newStartTime, dragRef.current.trackId, dragRef.current.layerId);
      } else if (isResizing && dragRef.current) {
        e.preventDefault();
        const deltaX = e.clientX - dragRef.current.startX;
        const deltaSeconds = deltaX / 10; // 10px per second
        const newDuration = Math.max(0.1, dragRef.current.startDuration + deltaSeconds);
        handleItemResize(dragRef.current.itemId, newDuration);
      }
    };

    const handleGlobalMouseUp = () => {
      setIsDragging(false);
      setIsResizing(false);
      dragRef.current = null;
    };

    if (isDragging || isResizing) {
      document.addEventListener('mousemove', handleGlobalMouseMove);
      document.addEventListener('mouseup', handleGlobalMouseUp);
    }

    return () => {
      document.removeEventListener('mousemove', handleGlobalMouseMove);
      document.removeEventListener('mouseup', handleGlobalMouseUp);
    };
  }, [isDragging, isResizing, handleItemMove, handleItemResize]);

  // Render timeline items for a track or layer
  const renderItems = (trackId: string, layerId?: string) => {
    return timelineItems
      .filter(item => item.trackId === trackId && item.layerId === layerId)
      .map(item => {
        const asset = getAssetById(item.assetId);
        if (!asset) return null;

        // Calculate item style based on startTime and duration (assuming 1 second = 10px for example)
        const left = item.startTime * 10;
        const width = item.duration * 10;

        const isSelected = selectedItemId === item.id;

        const onMouseDown = (e: React.MouseEvent<HTMLDivElement>) => {
          e.stopPropagation();
          setSelectedItemId(item.id);
          setSelectedNodeId(null);
          setSelectedConnectionId(null);
          setIsDragging(true);
          dragRef.current = { itemId: item.id, startX: e.clientX, startTime: item.startTime, startDuration: item.duration, trackId, layerId };
        };

        const onResizeMouseDown = (e: React.MouseEvent<HTMLDivElement>) => {
          e.stopPropagation();
          setIsResizing(true);
          dragRef.current = { itemId: item.id, startX: e.clientX, startTime: item.startTime, startDuration: item.duration, trackId, layerId };
        };

        return (
          <div
            key={item.id}
            className={`timeline-item ${isSelected ? 'selected' : ''}`}
            style={{
              position: 'absolute',
              left,
              width,
              top: 2,
              height: 'calc(100% - 4px)',
              backgroundColor: isSelected ? '#6366f1' : '#4b5563',
              borderRadius: 4,
              cursor: 'pointer',
              color: 'white',
              padding: '2px 6px',
              boxSizing: 'border-box',
              userSelect: 'none',
              overflow: 'hidden',
              whiteSpace: 'nowrap',
              textOverflow: 'ellipsis',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'space-between'
            }}
            onClick={() => handleItemSelect(item.id)}
